.content-3 {
  background-color:var(--main-purple);
  width: 100%;
  color: var(--main-yellow);
  padding-top: 1rem;
  padding-bottom: 3rem;

  @media (min-width: 600px) {
    bottom: 0;
    z-index: 8;
    padding-top: 0rem;
    padding-bottom: 5rem;
  }

  .sign-up {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    font-size: 16px;

    .input-wrapper {
      width: 70%;
      max-width: 350px;
      display: flex;
      flex-direction: column;
      margin-bottom: 1rem;

      @media (max-width: 300px) {
        width: 85%;
      }

      @media (min-width: 600px) {
         max-width: 280px;
      }

      label {
        padding: 0.4rem;
        font-weight: bold;
        filter: drop-shadow(0px 0px 0.18px #000000); 

        @media (min-width: 600px) {
          letter-spacing: 0.04rem;
        }   
      }

      input:not(:placeholder-shown) {
        border: 2px solid var(--main-yellow);
        background-color: var(--main-yellow);
        color: black;
      }

      input:not(:placeholder-shown) + .stroke {
        opacity: 0;
      }

      input {
        padding: 0.8rem;
        font-size: 16px;
        background-color: transparent;
        outline: 0;
        border: 2px solid transparent;
        border-radius: 1.5rem;
        color: var(--main-yellow);
        letter-spacing: 0.06;

        @media (max-width: 300px) {
          font-size: 12px;
        }

        @media (min-width: 600px) {
          letter-spacing: 0rem;
        } 

        &:focus + .stroke {
          opacity: 0;
          transition: all 0.5s ease-in-out;
        }

        input:placeholder-shown {
          background-color: var(--main-purple);
          transition: all 0.3s ease;  
        }

        &:not(:focus)  {
          transition: all 0.3s ease-in-out;
        }

        &::placeholder {
          color: transparent;
          transition: color 0.3s ease;
        }

        &:focus {
          background-color: var(--main-yellow);
          border-radius: 1.5rem;
          padding: 0.8rem;
          transition: all 0.5s ease-in-out;
          color: black;

          &::placeholder {
            color: rgb(74, 74, 74);
          }
        }
      }

      .stroke {
        width: 100%;
        height: 5px;
        background-color: var(--main-yellow);
        border-radius: 1rem;
        box-shadow: 0px 3px 6px rgba(0, 0, 0, 0.161);
        transition: all 0.3s ease-in-out;
      }
    }  
  }
}

.form-button {
  background-color: var(--main-yellow);
  outline: none;
  background-size: cover;
  width: 6rem;
  height: 6rem;
  border-radius: 50%;
  color: var(--main-purple);
  font-weight: bold;
  font-size: 1.2rem;
  border: none;
  margin-top: 2.5rem;
  transition: all 0.5s ease;
  cursor: pointer;
  -webkit-tap-highlight-color: transparent;

  &:hover{
    transform: scale(1.1);
    box-shadow: 0px 13px 30px rgba(0, 0, 0, 0.25);
  }

  &:active {
    transform: translateY(0.2rem);
    box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.25);
  }
}
